var title_f9_11_9404="Ovulation induction in PCOS";
var content_f9_11_9404=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F82084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F82084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison between laparoscopic ovulation induction and gonadotropin ovulation induction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Laparoscopy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gonadotropin administration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Advantages",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Pregnancy rates comparable to gonadotropins",
"        </p>",
"        <p>",
"         No additional procedures required",
"        </p>",
"        <p>",
"         Minimal monitoring required",
"        </p>",
"        <p>",
"         One treatment produces multiple ovulatory cycles",
"        </p>",
"        <p>",
"         Usually produces monovulatory cycles",
"        </p>",
"        <p>",
"         No increased risk of ovarian hyperstimulation",
"        </p>",
"        <p>",
"         No expensive medications required",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         High probability of successful ovulation induction",
"        </p>",
"        <p>",
"         Conception rates comparable to laparoscopy",
"        </p>",
"        <p>",
"         No known risk of adhesive damage",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Disadvantages",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         Initial surgery not entirely risk-free",
"        </p>",
"        <p>",
"         Adhesions",
"        </p>",
"        <p>",
"         Ovarian atrophy",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         High cost",
"        </p>",
"        <p>",
"         Intensive monitoring required",
"        </p>",
"        <p>",
"         One ovulatory event per cycle",
"        </p>",
"        <p>",
"         Increased risk of multiple gestations",
"        </p>",
"        <p>",
"         Increased risk of ovarian hyperstimulation",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
