" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Therapy of heart failure in hemodialysis patients",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/12/9416/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/12/9416/contributors\">",
"     Charles A Herzog, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/12/9416/contributors\">",
"     William L Henrich, MD, MACP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/12/9416/contributors\">",
"     Alfred K Cheung, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/12/9416/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/12/9416/contributors\">",
"     Jeffrey S Berns, MD",
"    </a>",
"    <br/>",
"   </div>",
