var title_f9_16_9484="CUP treatment schema";
var content_f9_16_9484=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F52585&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F52585&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatments for specific subsets of patients with CUP",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Histologic type",
"      </td>",
"      <td class=\"subtitle1\">",
"       Clinical feature",
"      </td>",
"      <td class=\"subtitle1\">",
"       Treatment recommendation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       Adenocarcinoma",
"      </td>",
"      <td>",
"       Women with isolated axillary adenopathy",
"      </td>",
"      <td>",
"       Treat as stage II breast cancer",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Women with peritoneal carcinomatosis",
"      </td>",
"      <td>",
"       Treat as stage III ovarian cancer",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Men with elevated PSA or blastic bone metastases",
"      </td>",
"      <td>",
"       Treat as advanced prostate cancer",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Colon cancer profile",
"      </td>",
"      <td>",
"       Treat as advanced colon cancer",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Adenocarcinoma or PDC",
"      </td>",
"      <td>",
"       Single metastatic lesion",
"      </td>",
"      <td>",
"       Definitive local therapy (resection and/or radiation therapy)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"3\">",
"       Squamous cell carcinoma",
"      </td>",
"      <td>",
"       Cervical adenopathy",
"      </td>",
"      <td>",
"       Treat as head and neck cancer with involved neck nodes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Inguinal adenopathy",
"      </td>",
"      <td>",
"       Inguinal node dissection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Consider concurrent radiation therapy/chemotherapy (as in locally advanced cervical cancer)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       Poorly differentiated carcinoma",
"      </td>",
"      <td>",
"       Young men with midline tumor or elevated hCG/AFP",
"      </td>",
"      <td>",
"       Treat as extragonadal germ cell tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Other clinical presentations",
"      </td>",
"      <td>",
"       Empiric platinum/paclitaxel chemotherapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       Poorly differentiated neuroendocrine carcinoma",
"      </td>",
"      <td>",
"       Diverse clinical presentations",
"      </td>",
"      <td>",
"       Treat with platinum/etoposide or paclitaxel/platinum/etoposide",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Approximately 40 percent of patients with carcinoma of unknown primary (CUP) fit into defined subsets with specific, recommended treatment implications.",
"    <div class=\"footnotes\">",
"     AFP: alpha-fetoprotein; hCG: human chorionic gonadotropin; PDC: poorly differentiated carcinoma; PSA: prostate-specific antigen.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
