var title_f9_17_9500="High potassium food PI";
var content_f9_17_9500=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F67156&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F67156&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Foods with high levels of potassium",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       <strong>",
"        Grains",
"       </strong>",
"      </td>",
"      <td>",
"       Whole-grain breads, wheat bran, granola and granola bars",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Beverages",
"       </strong>",
"      </td>",
"      <td>",
"       Sports drinks (Gatorade, etc.), instant breakfast mix, soy milk",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Snack foods/sweets",
"       </strong>",
"      </td>",
"      <td>",
"       Peanut butter (2 tablespoons), nuts or seeds (1 ounce), fig cookies, chocolate (1.5 to 2 ounces), molasses (1 tablespoon)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Fruits",
"       </strong>",
"      </td>",
"      <td>",
"       Apricots, avocado (&frac14; whole), bananas (&frac12; whole), coconut, melon (cantaloupe and honeydew), kiwi, mango, nectarines, oranges, orange juice, papaya, pears (fresh), plantains, pomegranate (and juice), dried fruits (apricots (5 halves), dates (5), figs, prunes, raisins), prune juice, yams",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Vegetables",
"       </strong>",
"      </td>",
"      <td>",
"       Bamboo shoots, baked or refried beans, beets, broccoli (cooked), Brussels sprouts, cabbage (raw), carrots (raw), chard, greens (except kale), kohlrabi, olives, mushrooms (canned), potatoes (white and sweet), parsnips, pickles, pumpkin, rutabaga, sauerkraut, spinach (cooked), squash (acorn, butternut, hubbard), tomato, tomato sauce, tomato juice, and vegetable juice cocktail",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Dairy products",
"       </strong>",
"      </td>",
"      <td>",
"       Milk and milk products, buttermilk, yogurt",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Proteins",
"       </strong>",
"      </td>",
"      <td>",
"       (3-ounce serving) Clams, sardines, scallops, lobster, whitefish, salmon (and most other fish), ground beef, sirloin steak (and most other beef products), pinto beans, kidney beans, black beans, navy beans (and most other peas and beans, serving size is &frac12; cup)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Soups",
"       </strong>",
"      </td>",
"      <td>",
"       Salt-free soups and low-sodium bouillon cubes, unsalted broth",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Condiments",
"       </strong>",
"      </td>",
"      <td>",
"       Imitation bacon bits, lite salt or salt substitutes (avoid completely)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Unless noted, one serving is &frac12; cup (4 ounces). These foods have greater than 250 mg of potassium per serving and should be avoided or eaten in very small portions if you have been told to eat a low-potassium diet.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
