var title_f9_17_9501="Indications for posthumous reproduction";
var content_f9_17_9501=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F83086&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F83086&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for posthumous reproduction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Scenario",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gamete provider(s)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Requester(s)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Techniques",
"       </td>",
"       <td class=\"subtitle1\">",
"        Considerations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Possibility of death is anticipated",
"       </td>",
"       <td>",
"        <p>",
"         Military personel in combat zones or other high risk occupations",
"         <sup>",
"          [2]",
"         </sup>",
"        </p>",
"        <p>",
"         Patients with potentially fatal illness (eg, cancer)",
"         <sup>",
"          [2]",
"         </sup>",
"        </p>",
"       </td>",
"       <td>",
"        The gamete provider and a surviving partner or family member",
"       </td>",
"       <td>",
"        Gamete/embryo cryopreservation",
"       </td>",
"       <td>",
"        Satisfies issues of informed consent from gamete provider prior to their death and respect for autonomy of the deceased",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Fertility preservation",
"       </td>",
"       <td>",
"        Patients are expected to survive a potentially gonadotoxic event, such as chemotherapy or radiation for cancer",
"       </td>",
"       <td>",
"        Usually a surviving partner or family member",
"       </td>",
"       <td>",
"        Cryopreservation of gametes, embryos, or reproductive tissues (eg, ovarian tissue)",
"       </td>",
"       <td>",
"        <p>",
"         Tissues are stored for future use in anticipation of probable cure",
"        </p>",
"        <p>",
"         Disposition of reproductive tissues should be decided at the time of their collection",
"         <sup>",
"          [1]",
"         </sup>",
"        </p>",
"        <p>",
"         Legal minors may not be able to give informed consent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        General infertility",
"       </td>",
"       <td>",
"        Individuals/couples previously or currently using assisted reproductive technologies for routine indications",
"       </td>",
"       <td>",
"        <p>",
"         Usually a surviving partner or family member",
"        </p>",
"        <p>",
"         Possibly an unknown recipient (in the case of gamete or embryo donation)",
"         <sup>",
"          [3]",
"         </sup>",
"        </p>",
"       </td>",
"       <td>",
"        Gamete/embryo cryopreservation",
"       </td>",
"       <td>",
"        <p>",
"         Cryopreserved gametes or embryos already stored for use by the intended parents while living",
"         <sup>",
"          [3]",
"         </sup>",
"        </p>",
"        <p>",
"         Desire to reproduce while alive is explicit, but wishes concerning posthumous reproduction are not always considered",
"        </p>",
"        <p>",
"         Disposition of gametes/embryos in case of death should be determined prior to freezing",
"         <sup>",
"          [1]",
"         </sup>",
"        </p>",
"        <p>",
"         Full disclosure and 3rd party consent required in cases of anonymous gamete/embryo donation",
"         <sup>",
"          [1]",
"         </sup>",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Sudden unexpected death or incapacitated state",
"       </td>",
"       <td>",
"        Patients suffering recent death, coma, or irrecoverable illness rendering them unable to provide consent for PAR",
"        <sup>",
"         [4]",
"        </sup>",
"       </td>",
"       <td>",
"        Surviving partner or family member",
"       </td>",
"       <td>",
"        Posthumous or perimortem gamete retrieval and cryopreservation",
"       </td>",
"       <td>",
"        <p>",
"         Often no opportunity to obtain consent from the prospective gamete donor",
"        </p>",
"        <p>",
"         Gamete retrieval can be invasive and is usually time-sensitive",
"        </p>",
"        <p>",
"         The act may be of emotionally therapeutic value for survivors even if the gametes are rarely if ever used",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <ol>",
"      <li>",
"       ESHRE Task Force on Ethics and Law, Pennings G, de Wert G, et al. ESHRE Task Force on Ethics and Law 11: Posthumous assisted reproduction. Hum Reprod 2006; 21:3050.",
"      </li>",
"      <li>",
"       Kindregan CP. Dead Dads: Thawing an Heir from the Freezer. William Mitchell Law Review 2009; 35:433.",
"      </li>",
"      <li>",
"       Ethics Committee of the American Society for Reproductive Medicine. Posthumous reproduction. Fertil Steril 2004; 82 Suppl 1:S260.",
"      </li>",
"      <li>",
"       Greer DM, Styer AK, Toth TL, et al. Case records of the Massachusetts General Hospital. Case 21-2010. A request for retrieval of oocytes from a 36-year-old woman with anoxic brain injury. N Engl J Med 2010; 363:276.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
