var title_f9_20_9549="Evaluation shoulder pain";
var content_f9_20_9549=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F53038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F53038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential history and examination of the shoulder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        History and epidemiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Examination findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Likely diagnosis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Generally age &gt;40",
"        </p>",
"        <p>",
"         Pain increases with reaching",
"        </p>",
"        Frequent repetitive activity at or above shoulder",
"       </td>",
"       <td>",
"        <p>",
"         Subacromial tenderness",
"        </p>",
"        <p>",
"         Pain with Apley scratch tests",
"        </p>",
"        <p>",
"         Normal passive range of motion",
"        </p>",
"        <p>",
"         Normal strength but pain with testing midarc abduction and/or external rotation",
"        </p>",
"        Pain with impingement testing (Neer and Hawkins tests)",
"       </td>",
"       <td>",
"        Rotator cuff tendinopathy (very common)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Same as rotator cuff tendinopathy, but weakness present",
"        </p>",
"        Midde aged and older",
"       </td>",
"       <td>",
"        Same as rotator cuff tendinopathy but weakness often present with resisted abduction and/or external rotation",
"       </td>",
"       <td>",
"        Rotator cuff tear",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Past history of rotator cuff tendinopathy, diabetes, or immobility for any reason",
"        </p>",
"        Complaint of decreased motion +/- pain",
"       </td>",
"       <td>",
"        Significant decrease in range of motion, both active and passive",
"       </td>",
"       <td>",
"        Adhesive capsulitis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Past history of shoulder trauma",
"       </td>",
"       <td>",
"        Decrease in range of motion - both active and passive",
"       </td>",
"       <td>",
"        Glenohumoral osteoarthritis (uncommon)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Pain increases when carrying objects with elbows bent (eg, shopping bags) or lifting overhead",
"       </td>",
"       <td>",
"        <p>",
"         Bicipital groove tenderness",
"        </p>",
"        Pain with resisted elbow flexion or supination",
"       </td>",
"       <td>",
"        Biceps tendinopathy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Sudden increase in shoulder pain with \"Popeye\" deformity (ie, prominent ipsilateral distal bicep)",
"       </td>",
"       <td>",
"        <p>",
"         Obvious biceps deformity",
"        </p>",
"        Pain with resisted elbow flexion or supination",
"       </td>",
"       <td>",
"        Biceps tendon rupture",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Recent fall onto adducted arm",
"        </p>",
"        Focal AC joint pain",
"       </td>",
"       <td>",
"        <p>",
"         AC joint tenderness with possible stepoff",
"        </p>",
"        <p>",
"         Pain with adduction of injured arm",
"        </p>",
"        Clavicle elevation on x-ray with higher grade sprain",
"       </td>",
"       <td>",
"        Acromioclavicular injury",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Focal AC joint pain without recent trauma",
"       </td>",
"       <td>",
"        <p>",
"         AC joint tenderness",
"        </p>",
"        Pain with adduction of injured arm",
"       </td>",
"       <td>",
"        Acromioclavicular osteoarthritis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Generally age &lt;40",
"        </p>",
"        <p>",
"         Overhead athletes",
"        </p>",
"        Nonspecific symptoms",
"       </td>",
"       <td>",
"        <p>",
"         Sulcus test shows increased motion",
"        </p>",
"        Apprehension, relocation, and release tests positive",
"       </td>",
"       <td>",
"        Multidirectional shoulder instability (may have concomitant rotator cuff tear)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Poor muscular development, frequent repetitive to-and-fro motion (eg, ironing), and direct pressure (eg, backpack) are common causes",
"       </td>",
"       <td>",
"        Superiomedial scapular border tender (performed with ipsilateral arm adducted)",
"       </td>",
"       <td>",
"        Subscapular bursitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sedentary (eg, works at desk job); poor posture with rounded upper back",
"       </td>",
"       <td>",
"        <p>",
"         Abnormal, uncoordinated scapulothoracic and glenohumoral motion",
"        </p>",
"        <p>",
"         Wall push off may reveal mild scapular winging",
"        </p>",
"        Stabilization of scapula by examiner improves shoulder strength",
"       </td>",
"       <td>",
"        Scapular stabilizer muscle weakness",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
