" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Drugs that affect bone metabolism",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
