var title_f9_22_9580="Urethral CA TNM women";
var content_f9_22_9580=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F62556&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F62556&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM staging system for urethral cancer in women",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Primary tumor (T)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TX",
"      </td>",
"      <td>",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T0",
"      </td>",
"      <td>",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ta",
"      </td>",
"      <td>",
"       Noninvasive papillary, polypoid, or verrucous carcinoma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Tis",
"      </td>",
"      <td>",
"       Carcinoma in situ",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       Tumor invades subepithelial connective tissue",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       Tumor invades periurethral muscle",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       Tumor invades anterior vagina, bladder neck",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4",
"      </td>",
"      <td>",
"       Tumor invades other adjacent organs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Regional lymph nodes (N)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       NX",
"      </td>",
"      <td>",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N1",
"      </td>",
"      <td>",
"       Metastasis in a single lymph node 2 cm or less in greatest dimension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N2",
"      </td>",
"      <td>",
"       Metastasis in a single node more than 2 cm in greatest dimension, or in multiple nodes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Distant metastasis (M)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M0",
"      </td>",
"      <td>",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M1",
"      </td>",
"      <td>",
"       Distant metastasis",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
