var title_f9_23_9595="Questions used to assess possibility of pregnancy";
var content_f9_23_9595=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F67567&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F67567&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Excluding pregnancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Pregnancy can be reasonably excluded if a client answers YES to any of the following questions:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ol>",
"         <li>",
"          Have you abstained from sexual intercourse since your last menstrual period?",
"         </li>",
"         <li>",
"          Have you had a normal menstrual period within the past 7 days?",
"         </li>",
"         <li>",
"          Have you been using a reliable form of contraception consistently and correctly?",
"         </li>",
"         <li>",
"          Have you had a miscarriage or abortion within the past 7 days?",
"         </li>",
"         <li>",
"          Have you had a baby within the past 4 weeks?",
"         </li>",
"         <li>",
"          Have you had a baby within the past 6 months AND are you fully or nearly-fully breastfeeding AND have you not had a menstrual period since the delivery?",
"         </li>",
"        </ol>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: a checklist developed by Family Health International in 2008.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
