var title_f9_24_9606="NTM clavicle osteomyelitis on CT scan";
var content_f9_24_9606=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F85857&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F85857&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 596px\">",
"   <div class=\"ttl\">",
"    CT scan of the chest showing osteomyelitis of the right clavicle due to M. abscessus",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 576px; height: 435px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    CT: Computed tomography.",
"    <br>",
"     <br>",
"      CT scan of a 55-year-old man with disseminated M. abscessus.&nbsp;The infection began in the left ankle and seeded multiple other bones including the right clavicle.&nbsp;The CT scan demonstrates advanced destruction of the right distal clavicle with bony fragmentation and an associated soft tissue mass.&nbsp;The histopathology at the time of debridement showed granulomas.&nbsp;This case supports the theory of locus minoris resistentiae, since this patient had received local radiation therapy to his right chest wall thirty years earlier for Hodgkin lymphoma.&nbsp;",
"      <div class=\"footnotes\">",
"      </div>",
"      <div class=\"reference\">",
"       Courtesy of Dr. Shannon Kasperbauer and Dr. Charles Daley.",
"      </div>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
