var title_f9_25_9619="Three types of holoprosencephaly";
var content_f9_25_9619=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F55559&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F55559&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Three types of holoprosencephaly",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 203px; height: 600px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    (A) Alobar holoprosencephaly consisting of a single ventricle, fused thalami, and absence of the midline structures (falx cerebri, corpus callosum, cavum septum pellucidum).",
"    <br>",
"     (B) Semilobar holoprosencephaly consisting of a single ventricle, incomplete fusion of the thalami, and partially separated lateral ventricles. The arrow points to a small falx cerebri.",
"     <br>",
"      (C) Lobar holoprosencephaly consisting of fusion of the anterior horns of the lateral ventricle and absent cavum septum pellucidum.",
"      <div class=\"footnotes\">",
"       T: thalami.",
"      </div>",
"      <div class=\"reference\">",
"       Courtesy of Ana Monteagudo, MD.",
"      </div>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
