" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Group B streptococcus: Virulence factors and pathogenic mechanisms",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/25/9621/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/25/9621/contributors\">",
