var title_f9_26_9644="Duke criteria for IE I";
var content_f9_26_9644=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F73023&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F73023&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Modified Duke criteria for diagnosis of infective endocarditis - table A",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Definite IE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Pathologic criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Microorganism:",
"demonstrated by culture or histology in a vegetation, or in a",
"vegetation that has embolized, or in an intracardiac abscess",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        Pathologic lesions: vegetation or intracardiac abscess, confirmed by histology showing active endocarditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Clinical criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Using specific definitions listed in",
"        <strong>",
"         Table B:",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2 major criteria",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1 major and 3 minor criteria",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        5 minor criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Possible IE*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 major criterion and 1 minor criterion",
"        <strong>",
"         OR",
"        </strong>",
"        3 minor criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Rejected IE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Firm alternate diagnosis for manifestations of endocarditis",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Resolution of manifestations of endocarditis, with antibiotic therapy for four days or less",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        No pathologic evidence of infective endocarditis at surgery or autopsy after antibiotic therapy for four days or less",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Does not meet criteria for possible infective endocarditis, as above",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * The category of possible IE represents a modification from the previous published Duke criteria.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Li JS, Sexton DJ, Mick N, et al. Clin Infect Dis 2000; 30:633. Copyright &copy; 2000 University of Chicago Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
