var title_f9_26_9645="Phenytoin interactions with other AEDs";
var content_f9_26_9645=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F55399&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F55399&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Some interactions between phenytoin and other antiepileptic drugs*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Interacting drugs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effects (probable mechanism)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Phenytoin, with:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Carbamazepine",
"       </td>",
"       <td>",
"        Decreased carbamazepine effect (increased metabolism)",
"       </td>",
"       <td rowspan=\"2\">",
"        Monitor carbamazepine and phenytoin concentrations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Altered phenytoin effect (mechanism not established)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Clonazepam",
"       </td>",
"       <td>",
"        Decreased clonazepam effect (increased metabolism)",
"       </td>",
"       <td>",
"        Monitor clonazepam effect or concentrations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Variable effects on phenytoin concentrations (mechanism not established)",
"       </td>",
"       <td>",
"        Monitor phenytoin concentrations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lamotrigine",
"       </td>",
"       <td>",
"        Decreased lamotrigine serum concentrations (increased metabolism; induction of glucuronidation by phenytoin)",
"       </td>",
"       <td>",
"        Monitor lamotrigine serum concentrations and clinical status; lamotrigine dosage may need to be adjusted",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Oxcarbazepine",
"       </td>",
"       <td>",
"        Possible decreased oxcarbazepine effect (increased metabolism)",
"       </td>",
"       <td>",
"        Monitor clinical status and serum concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Possible phenytoin toxicity with oxcarbazepine doses of 1200 mg/day or higher (decreased metabolism)",
"       </td>",
"       <td>",
"        Monitor phenytoin concentrations especially when oxcarbazepine dosage is 1200 mg/day or higher",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Perampanel",
"       </td>",
"       <td>",
"        Decreased perampanel effect (increased metabolism by CYP3A4)",
"       </td>",
"       <td>",
"        Monitor clinical status; perampanel dosage may need to be adjusted",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tiagabine",
"       </td>",
"       <td>",
"        Decreased tiagabine effect (increased metabolism)",
"       </td>",
"       <td>",
"        Monitor clinical status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Topiramate",
"       </td>",
"       <td>",
"        Possible decreased topiramate effect (increased metabolism)",
"       </td>",
"       <td>",
"        Monitor topiramate concentrations and clinical status; may need to increase topiramate dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Possible phenytoin toxicity (decreased metabolism by CYP2C19)",
"       </td>",
"       <td>",
"        Monitor phenytoin concentrations and clinical status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Valproate",
"       </td>",
"       <td>",
"        Possible phenytoin toxicity (displacement from binding)",
"       </td>",
"       <td>",
"        Monitor phenytoin concentrations and clinical status (unbound concentrations may be more helpful than total)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Possible decreased valproate effect and increased toxicity (increased metabolism and formation of toxic metabolite)",
"       </td>",
"       <td>",
"        Monitor clinical status and valproate serum concentrations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Zonisamide",
"       </td>",
"       <td>",
"        Decreased zonisamide effect (increased metabolism by CYP3A4)",
"       </td>",
"       <td>",
"        Monitor zonisamide concentrations and clinical status; zonisamide dosage may need to be adjusted",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *",
"     <strong>",
"      NOTE: Not all potential interactions are listed.",
"     </strong>",
"     Additional interactions of antiepileptic drugs and management suggestions may be determined using the drug interactions tool (Lexi-Interact Online) included in UpToDate. The Lexi-Interact program can be accessed from the UpToDate new search tab or through the individual drug information topics in the section on Drug interactions.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Drugs for Epilepsy. Treatment guidelines from The Medical Letter 2008; 6(70):37-46. Copyright &copy; 2008 The Medical Letter.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
