"   <div class=\"ord-stmt war-os drugH1Div\" id=\"F10031295\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Warning",
"    </span>",
"    <ul class=\"statements\" style=\"list-style-type:none;\">",
"     <li class=\"statement\">",
"      <span class=\"attributes\" style=\"display:none;\">",
"       <span class=\"entity\">",
