" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Etiology, clinical features, and evaluation of chronic mitral regurgitation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/30/9705/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/30/9705/contributors\">",
"     Catherine M Otto, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/30/9705/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/30/9705/contributors\">",
"     William H Gaasch, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/30/9705/contributors\">",
"     Warren J Manning, MD",
"    </a>",
"    <br/>",
"   </div>",
