var title_f9_33_9755="Toxic blood lead levels children";
var content_f9_33_9755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F64183&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F64183&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Blood lead levels considered toxic",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Prior to 1971",
"       </td>",
"       <td>",
"        &ge; 60 mcg/dL (2.88 micromol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        1972-1975",
"       </td>",
"       <td>",
"        &ge; 40 mcg/dL (1.93 micromol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        1975-1985",
"       </td>",
"       <td>",
"        &ge; 30&nbsp;mcg/dL (1.45 micromol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        1985-1991",
"       </td>",
"       <td>",
"        &ge; 25&nbsp;mcg/dL (1.20 micromol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        1991-present",
"       </td>",
"       <td>",
"        &ge; 10&nbsp;mcg/dL (0.48 micromol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        2010-present",
"       </td>",
"       <td>",
"        &ge; 5 mcg/dL (0.24 micromol/L)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     mcg: micrograms; dL: deciliter; micromol: micromoles; L: liter.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
