var title_f9_39_9853="Food-borne diseases II";
var content_f9_39_9853=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53848&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53848&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for confirmation of food-borne disease outbreaks-II",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiologic agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incubation period",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical syndrome",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Bacterial (cont.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        <em>",
"         Clostridium perfringens",
"        </em>",
"       </td>",
"       <td rowspan=\"3\">",
"        6-24 hrs",
"       </td>",
"       <td rowspan=\"3\">",
"        Diarrhea, abdominal cramps; vomiting and fever are uncommon",
"       </td>",
"       <td>",
"        Isolation of &ge;10",
"        <sup>",
"         6",
"        </sup>",
"        organisms/g in stool of two or more ill persons, provided specimen properly handled",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Demonstration of enterotoxin in the stool of two or more ill persons",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist2_start\" colspan=\"4\">",
"        <em>",
"         Escherichia coli",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\" rowspan=\"3\">",
"        Enterohemorrhagic (EHEC -",
"        <em>",
"         E. coli",
"        </em>",
"        0157:H7 and others)",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        1-10 days, usually 3-4 days",
"       </td>",
"       <td class=\"sublist_other\" rowspan=\"3\">",
"        Diarrhea (often bloody), abdominal cramps (often severe), little or no fever",
"       </td>",
"       <td class=\"sublist_other\">",
"        Isolation of",
"        <em>",
"         E. coli",
"        </em>",
"        O157:H7 or other Shiga toxin-producing",
"        <em>",
"         E. coli",
"        </em>",
"        from clinical specimen of two or more ill persons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        Isolation of",
"        <em>",
"         E. coli",
"        </em>",
"        O157 or other Shiga toxin-producing",
"        <em>",
"         E. coli",
"        </em>",
"        from epidemiologically implicated food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Enterotoxigenic (ETEC)",
"       </td>",
"       <td class=\"sublist_other\">",
"        6-48 hrs",
"       </td>",
"       <td class=\"sublist_other\">",
"        Diarrhea, abdominal cramps, nausea; vomiting and fever are less common",
"       </td>",
"       <td class=\"sublist_other\">",
"        Isolation of organism of same serotype, which are demonstrated to produce heat-stable (ST) and/or heat-liable (LT) enterotoxin, from stool of two or more ill persons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Enteropathogenic (EPEC)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Variable",
"       </td>",
"       <td class=\"sublist_other\">",
"        Diarrhea, fever, abdominal cramps",
"       </td>",
"       <td class=\"sublist_other\">",
"        Isolation of same enteropathogenic serotype from stool of two or more ill persons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Enteroinvasive variable (EIEC)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Variable",
"       </td>",
"       <td class=\"sublist_other\">",
"        Diarrhea (may be bloody), fever, abdominal cramps",
"       </td>",
"       <td class=\"sublist_other\">",
"        Isolation of same enteroinvasive serotype from stool of two or more ill persons",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist2_start\" colspan=\"4\">",
"        <em>",
"         Listeria monocytogenes",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Invasive disease",
"       </td>",
"       <td class=\"sublist_other\">",
"        2-6 weeks",
"       </td>",
"       <td class=\"sublist_other\">",
"        Meningitis, neonatal sepsis, fever",
"       </td>",
"       <td class=\"sublist_other\">",
"        Isolation of organism from normally sterile site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Diarrheal disease",
"       </td>",
"       <td class=\"sublist_other\">",
"        Unknown",
"       </td>",
"       <td class=\"sublist_other\">",
"        Diarrhea, abdominal cramps, fever",
"       </td>",
"       <td class=\"sublist_other\">",
"        Isolation of organism of same serotype from stool of two or more ill persons exposed to food that is epidemiologically implicated or from which organism of same serotype has been isolated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: file://www.cdc.gov/mmwr//mmwr.html.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
