var title_f9_4_9293="Amsterdam Forum Guide B";
var content_f9_4_9293=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F53735&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F53735&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Amsterdam forum guidelines for screening living potential kidney donors - 2",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Urine analysis for blood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with persistent microscopic hematuria should not be considered for kidney donation unless urine cytology and a complete urologic work up are performed. If urological malignancy and stone disease are excluded, a kidney biopsy may be indicated to rule out glomerular pathology, such as IgA nephropathy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diabetes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individuals with a history of diabetes or fasting blood glucose &ge;126 mg/dL (7.0 nmol/L) on at least two occasions (or 2-hr glucose with OGTT &ge;200 mg/dL (11.1 mmol/L) should not donate.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stone disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        An asymptomatic potential donor with history of a single stone may be suitable for kidney donation if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        No hypercalcuria, hyperuricemia, or metabolic acidosis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        No cystinuria, or hyperoxaluria.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        No urinary tract infection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        If multiple stones or nephrocalcinosis are not evident on CT.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        An asymptomatic potential donor with a current single stone may be suitable if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        The donor meets the criteria shown previously for single stone formers and current stone &lt;1.5 cm in size, or potentially removable during the transplant.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Stone formers who should not donate are those with:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Nephrocalcinosis on x-ray or bilateral stone disease.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Stone types with high recurrence rates, and are difficult to prevent (see text).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A prior history of the following malignancies usually excludes live kidney donation: melanoma, testicular cancer, renal cell carcinoma, choriocarcinoma, hematological malignancy, bronchial cancer, breast cancer and monoclonal gammopathy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        A prior history of malignancy may only be acceptable for donation if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Prior treatment of the malignancy does not decrease renal reserve or place the donor at increased risk for ESRD.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Prior treatment of malignancy does not increase the operative risk of nephrectomy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        A prior history of malignancy usually excludes live kidney donation but may be acceptable if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        The specific cancer is curable and potential transmission of cancer can reasonably be excluded.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Urinary tract infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The donor urine should be sterile prior to donation; asymptomatic bacteria should be treated per donation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pyuria and hematuria at the proposed time of donation is a contraindication to donation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unexplained hematuria or pyuria necessitates evaluation for adenovirus, tuberculosis, and cancer.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinary tuberculosis or cancer are contraindications to donation.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BP: blood pressure; ABPM: ambulatory blood pressure monitoring; GFR: glomerular filtration rate; BMI: body mass index; BSA: body surface area; CT: computed tomography; ESRD: end-stage renal disease; HLA: human leukocyte antigen.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Delmonico F; Council of the Transplantation Society. A Report of the Amsterdam Forum On the Care of the Live Kidney Donor: Data and Medical Guidelines. Transplantation 2005; 79:S53. Copyright &copy; 2005 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
