var title_f9_4_9294="Rx entero endocarditisII";
var content_f9_4_9294=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F64258&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F64258&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested regimens for therapy of native valve endocarditis due to enterococcal strains resistant to penicillin and susceptible to aminoglycoside and vancomycin",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        American Heart Association",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        British Society for Antimicrobial Chemotherapy",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        European Society of Cardiology*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Adult",
"        <br/>",
"        (for patients with normal renal function)",
"       </td>",
"       <td class=\"subtitle2\">",
"        Pediatric",
"        <br/>",
"        (not to exceed dose of normal adult)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          &beta;-Lactamase producing:",
"         </strong>",
"        </p>",
"        <p>",
"         <strong>",
"          One of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &bull;",
"         </sup>",
"         3 mg/kg per 24&nbsp;hours IV or IM in&nbsp;three equally divided doses for&nbsp;six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          plus either",
"         </strong>",
"        </p>",
"        <p>",
"         Ampicillin-sulbactam",
"         <sup>",
"          &Delta;",
"         </sup>",
"         12 g per 24 hours IV in&nbsp;four equally divided doses for six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         30 mg/kg per 24 hours IV in&nbsp;two equally divided doses for six weeks; not to exceed 2 g per 24 hours unless concentrations in serum are inappropriately low",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          &beta;-Lactamase producing:",
"         </strong>",
"        </p>",
"        <p>",
"         <strong>",
"          One of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &bull;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in&nbsp;three equally divided doses for six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          plus either",
"         </strong>",
"        </p>",
"        <p>",
"         Ampicillin-sulbactam",
"         <sup>",
"          &Delta;",
"         </sup>",
"         300 mg/kg per 24 hours IV in&nbsp;four divided doses for six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         40 mg/kg per 24 hours IV in&nbsp;two or&nbsp;three equally divided doses for six weeks",
"        </p>",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         <strong>",
"          One of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &yen;",
"         </sup>",
"         1 mg/kg IV every 8 to 12 hours for &ge;4 weeks",
"        </p>",
"        <p>",
"         <strong>",
"          plus either",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         1 g IV every 12 hours for &ge;4 weeks",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Teicoplanin",
"         <sup>",
"          ",
"         </sup>",
"         10 mg/kg IV once daily for &ge;4 weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          &beta;-Lactamase producing:",
"         </strong>",
"        </p>",
"        <p>",
"         <strong>",
"          One of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &bull;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in&nbsp;two or&nbsp;three equally divided doses for&nbsp;four to&nbsp;six weeks",
"         <sup>",
"          ",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          plus either",
"         </strong>",
"        </p>",
"        <p>",
"         Ampicillin-sulbactam",
"         <sup>",
"          &Delta;",
"         </sup>",
"         12 g per 24 hours (or 300 mg/kg per 24 hours) IV in&nbsp;four equally divided doses for four&nbsp;to six weeks",
"         <sup>",
"          ",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Amoxicillin-clavulanate",
"         <sup>",
"          &Delta;",
"         </sup>",
"         200 mg/kg (amoxicillin component) per 24 hours IV in&nbsp;six equally divided doses for&nbsp;four to&nbsp;six weeks",
"         <sup>",
"          ",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         30 mg/kg per 24 hours IV in&nbsp;two equally divided doses for&nbsp;six weeks",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          Intrinsic penicillin resistance",
"         </strong>",
"         <sup>",
"          &sect;",
"         </sup>",
"         <strong>",
"          :",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         30 mg/kg per 24 hours IV in&nbsp;two divided doses for six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          plus",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &bull;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in&nbsp;three equally divided doses for six weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Intrinsic penicillin resistance",
"         </strong>",
"         <sup>",
"          &sect;",
"         </sup>",
"         <strong>",
"          :",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         40 mg/kg per 24 hours IV in&nbsp;two or three&nbsp;divided doses for six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          plus",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &bull;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in&nbsp;three equally divided doses for six weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Intrinsic penicillin resistance",
"         </strong>",
"         <sup>",
"          &sect;",
"         </sup>",
"         <strong>",
"          :",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &loz;",
"         </sup>",
"         30 mg/kg per 24 hours IV in&nbsp;two equally divided doses for&nbsp;six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          plus",
"         </strong>",
"        </p>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &bull;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in&nbsp;two or&nbsp;three equally divided doses for&nbsp;six weeks",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IV: intravenously; IM: intramuscularly.",
"     <br>",
"      * Pediatric doses (should not exceed adult doses): amoxicillin-clavulanate 300 mg/kg (amoxicillin component) per 24 hours IV in&nbsp;four or six divided doses; ampicillin-sulbactam, gentamicin and vancomycin dosing are same as recommended by American Heart Association, pediatric.",
"      <br>",
"       <font class=\"bullet\">",
"        &bull;",
"       </font>",
"       Renal function and serum gentamicin concentrations should be monitored at least once per week. Gentamicin dosage adjusted for peak serum concentration 3 to 4 mcg/mL, trough &lt;1 mcg/mL.",
"       <br>",
"        &Delta; If strain is gentamicin resistant, then &gt;6 weeks of ampicillin-sulbactam or amoxicillin-clavulanate therapy will be needed.",
"        <br>",
"         <font class=\"lozenge\">",
"          &loz;",
"         </font>",
"         Vancomycin therapy only recommended for patients unable to tolerate penicillins or intrinsic penicillin resistance; vancomycin dose adjusted for peak serum concentrations (one hour after infusion) of 30 to 45 mcg/mL and trough concentration of 10 to 15 mcg/mL.",
"         <br>",
"          &sect; Consultation with a specialist in infectious diseases recommended.",
"          <br>",
"           &yen; If gentamicin-susceptible (MIC &le;128 mg/L).",
"           <br>",
"            &Dagger; Alternative for penicillin-allergic patient provided isolate is teicoplanin susceptible (MIC &le;4 mg/L).",
"            <br>",
"             &dagger;&nbsp;Four weeks of&nbsp;therapy recommended for patients with symptoms of illness &le;3 months;&nbsp;six weeks of&nbsp;therapy recommended for patients with symptoms &gt;3 months. Intravenous amoxicillin-clavulanate is not available in United States.",
"            </br>",
"           </br>",
"          </br>",
"         </br>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Circulation 2005; 111:3167; J Antimicrob Chemother 2004; 54:971; Eur Heart J 2009; 30:2369.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
