" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Screening for colorectal cancer: Strategies in patients with possible increased risk due to family history",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/41/9881/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/41/9881/contributors\">",
"     Robert H Fletcher, MD, MSc",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/41/9881/contributors\">",
"     Scott D Ramsey, MD",
"    </a>",
"    <br/>",
"   </div>",
