var title_f9_41_9883="Ramsay sedation scale";
var content_f9_41_9883=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F65842&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F65842&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The Ramsay sedation scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Clinical score",
"      </td>",
"      <td class=\"subtitle1\">",
"       Patient characteristics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1",
"      </td>",
"      <td>",
"       Awake; Agitated or restless or both",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2",
"      </td>",
"      <td>",
"       Awake; Cooperative, oriented, and tranquil",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3",
"      </td>",
"      <td>",
"       Awake but responds to commands only",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4",
"      </td>",
"      <td>",
"       Asleep; brisk response to light glabellar tap or loud auditory stimulus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5",
"      </td>",
"      <td>",
"       Asleep; sluggish response to light glabellar tap or loud auditory stimulus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6",
"      </td>",
"      <td>",
"       Asleep; no response to glabellar tap or loud auditory stimulus",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from Ramsay, MA, Savage, TM, Simpson, BR, Goodwin, R, Br Med J 1974; 2:656.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
