var title_f9_41_9884="Techniques of termination phase of Interpersonal Psychotherapy";
var content_f9_41_9884=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F67730&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F67730&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Techniques of the termination phase of Interpersonal Psychotherapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Technique",
"       </td>",
"       <td class=\"subtitle1\">",
"        Strategy",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assess treatment progress and reinforce treatment gains",
"       </td>",
"       <td>",
"        Review serial scores on standardized, self-report depression rating instrument and gains made in interpersonal problem area over the course of therapy.",
"       </td>",
"       <td>",
"        Highlight treatment gains, which are usually considerable, and acknowledge residual symptoms.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discuss remaining treatment needs and options",
"       </td>",
"       <td>",
"        Evaluate need for ongoing psychotherapy, augmentation with other treatment modalities such as pharmacotherapy, or change of treatment strategies.",
"       </td>",
"       <td>",
"        Consider ongoing, monthly, maintenance IPT to consolidate and preserve treatment gains.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anticipate future vulnerability to recurrence of depression",
"       </td>",
"       <td>",
"        Revisit the link between one's current life events and depression, to help the patient identify periods of risk and strategies to prevent recurrence of symptoms.",
"       </td>",
"       <td>",
"        Establish a plan for seeking treatment in the future if depression recurs, ie, recontact the clinician or contact the primary care physician.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discuss feelings associated with termination",
"       </td>",
"       <td>",
"        Normalize feelings of sadness and loss arising at the end of psychotherapy, and differentiate these feelings from symptoms of depression.",
"       </td>",
"       <td>",
"        Probe for feelings associated with termination even if patient does not spontaneously volunteer them.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blame the therapy&mdash;and not the patient&mdash;for incomplete remission",
"       </td>",
"       <td>",
"        If therapy has failed to bring about complete resolution of the depressive episode, blame the therapy and not the patient.",
"       </td>",
"       <td>",
"        The clinician should remain optimistic, and encourage the patient to continue treatment of the depressive episode. The availability of numerous pharmacotherapy and psychotherapy options should be emphasized.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
