var script_f9_42_9890=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function StoolsOsmolality_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.StoolsOsmolality_form){",
"",
"",
"doCalc = true;",
"if (Stool_Osm_param.value.indexOf(',') >= 0){ Stool_Osm_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Stool_Osm_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Stool_Osm_unit.options[Stool_Osm_unit.selectedIndex].value.split('|');",
"Stool_Osm = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Stool_Na_param.value.indexOf(',') >= 0){ Stool_Na_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Stool_Na_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Stool_Na_unit.options[Stool_Na_unit.selectedIndex].value.split('|');",
"Stool_Na = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Stool_K_param.value.indexOf(',') >= 0){ Stool_K_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Stool_K_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Stool_K_unit.options[Stool_K_unit.selectedIndex].value.split('|');",
"Stool_K = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"SOG =  Stool_Osm - (2 * (Stool_Na + Stool_K));",
"",
"unit_parts = SOG_unit.options[SOG_unit.selectedIndex].value.split('|');",
"if (doCalc) SOG_param.value = fixDP((SOG - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"if (doCalc){",
"",
"rrclr();",
"if (SOG < 50){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((SOG >= 50) && (SOG <= 125)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"if (SOG > 125){ document.getElementById('rr1_3').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.StoolsOsmolality_form){",
"",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.StoolsOsmolality_form){",
"",
"SOG_param.value = '';",
"",
"rrclr();",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"}",
"",
"",
"/* ]]> */"].join("\n");
