var script_f9_43_9906=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function StrokeRiskFramingham_M_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.StrokeRiskFramingham_M_form){",
"",
"",
"doCalc = true;",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Age_unit.options[Age_unit.selectedIndex].value.split('|');",
"Age = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Sys_BP_param.value.indexOf(',') >= 0){ Sys_BP_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Sys_BP_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Sys_BP_unit.options[Sys_BP_unit.selectedIndex].value.split('|');",
"Sys_BP = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"HTN_Med = parseFloat(HTN_Med_pulldown.options[HTN_Med_pulldown.selectedIndex].value);",
"DM = parseFloat(DM_pulldown.options[DM_pulldown.selectedIndex].value);",
"Cig = parseFloat(Cig_pulldown.options[Cig_pulldown.selectedIndex].value);",
"CVD = parseFloat(CVD_pulldown.options[CVD_pulldown.selectedIndex].value);",
"AFib = parseFloat(AFib_pulldown.options[AFib_pulldown.selectedIndex].value);",
"LVH = parseFloat(LVH_pulldown.options[LVH_pulldown.selectedIndex].value);",
"Risk_Period_Factor = parseFloat(Risk_Period_Factor_pulldown.options[Risk_Period_Factor_pulldown.selectedIndex].value);",
"dp = decpts.options[decpts.selectedIndex].text;",
"Risk_Factor_Diff =  (Age * 0.0505) + (Sys_BP * 0.014) + HTN_Med + DM + Cig + CVD + AFib + LVH - 5.677;",
"",
"if (doCalc) Risk_Factor_Diff_param.value = fixDP(Risk_Factor_Diff, dp);",
"",
"",
"",
"Risk_During_Period =  100 * (1 - power(Risk_Period_Factor, eTo(Risk_Factor_Diff)));",
"",
"unit_parts = Risk_During_Period_unit.options[Risk_During_Period_unit.selectedIndex].value.split('|');",
"if (doCalc) Risk_During_Period_param.value = fixDP((Risk_During_Period - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.StrokeRiskFramingham_M_form){",
"",
"if (Age_param.value && Age < 55) {",
"Age = 0;",
"Age_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Age is 55 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Age_param.value && Age > 84) {",
"Age_param.value = \"\";",
"clrResults();",
"Age = 0;",
"doCalc = false;",
"alert(\"The maximum value for Age is 84 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.StrokeRiskFramingham_M_form){",
"",
"Risk_Factor_Diff_param.value = '';",
"Risk_During_Period_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
