"   <div class=\"list fbnlist drugH1Div drugBrandNames\" id=\"F4369824\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     International Brand Names",
"    </span>",
"    <ul>",
"     <li>",
"      Anseren (IT);",
"     </li>",
"     <li>",
"      Ansieten (AR);",
"     </li>",
"     <li>",
"      Ansietil (CL);",
"     </li>",
"     <li>",
"      Marcen (ES);",
"     </li>",
"     <li>",
"      Sedatival (CL);",
"     </li>",
"     <li>",
"      Sedotime (ES);",
