var title_f9_44_9933="Infectious causes generalized lymphadenopathy children";
var content_f9_44_9933=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F81033&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F81033&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Infectious causes of generalized lymphadenopathy in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Infectious",
"       </td>",
"       <td class=\"subtitle1\">",
"        Selected clinical features that may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Viral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Epstein-Barr virus",
"       </td>",
"       <td>",
"        Tonsillopharyngitis, splenomegaly (&gt;50 percent), fever, malaise, fatigue; periorbital edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cytomegalovirus",
"       </td>",
"       <td>",
"        Fever, malaise, fatigue, occasional hepatosplenomegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Herpes simplex virus",
"       </td>",
"       <td>",
"        Grouped vesicles; gingivostomatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Varicella zoster virus",
"       </td>",
"       <td>",
"        Generalized vesicular rash appearing in crops",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adenovirus",
"       </td>",
"       <td>",
"        Respiratory tract symptoms, pharyngitis, conjunctivitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rubella",
"       </td>",
"       <td>",
"        Fever and rash; may be asymptomatic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis B virus",
"       </td>",
"       <td>",
"        High-risk sexual behavior, exposure to blood products",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rubeola (measles)",
"       </td>",
"       <td>",
"        Maculopapular rash with cranial to caudal progression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Human immunodeficiency virus",
"       </td>",
"       <td>",
"        Recurrent bacterial infection, opportunistic infection, fever, diarrhea, encephalopathy, failure to thrive, hepatosplenomegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Fungal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Coccidioidomycosis (valley fever)",
"       </td>",
"       <td>",
"        Pneumonia; travel to or residence in endemic area (eg, southwestern United States)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Blastomycosis",
"       </td>",
"       <td>",
"        Pneumonia; travel to or residence in an endemic area (eg, southeastern and south central United States, Africa)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Bacterial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Group A streptococcal disease",
"       </td>",
"       <td>",
"        Rash followed by desquamation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Brucellosis",
"       </td>",
"       <td>",
"        Fever, sweats, malaise, fatigue, weight loss, ingestion of unpasteurized milk; exposure to cattle, sheep, or goats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tularemia",
"       </td>",
"       <td>",
"        Fever, chills, headache; ingestion of undercooked meats, exposure to rabbits, rodents, biting flies, or mosquitoes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leptospirosis",
"       </td>",
"       <td>",
"        Fever, rigors, myalgia, headache, conjunctival injection, rash, hepatosplenomegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Spirochetal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Syphilis",
"       </td>",
"       <td>",
"        Rash, fever, malaise, anorexia, and weight loss, hepatomegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lyme disease",
"       </td>",
"       <td>",
"        Erythema migrans, fever, headache, myalgia, malaise, arthralgia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Parasitic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Toxoplasmosis",
"       </td>",
"       <td>",
"        Most infections in immunocompetent hosts are asymptomatic; myalgia, fatigue, fever, splenomegaly, and maculopapular rash may be present; exposure to cats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leishmaniasis",
"       </td>",
"       <td>",
"        Cutaneous lesions, organomegaly, fever, cachexia; exposure to sandflies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Malaria",
"       </td>",
"       <td>",
"        Fever, travel to or residence in an endemic area",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Malley R. Lymphadenopathy. In: Textbook of Pediatric Emergency Medicine, 5th ed, Fleisher GR, Ludwig S, Henretig FM (Eds), Lippincott Williams and Wilkins, Philadelphia 2006. p.421.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
