var title_f9_48_9996="MDS classification WHO";
var content_f9_48_9996=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F71654&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F71654&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    World Health Organization (WHO) classification system for myelodysplastic syndrome (MDS)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Disease",
"      </td>",
"      <td class=\"subtitle1\">",
"       Marrow blasts",
"      </td>",
"      <td class=\"subtitle1\">",
"       Clinical presentation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Cytogenetic abnormalities",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Refractory anemia (RA)",
"      </td>",
"      <td>",
"       &lt;5 percent",
"      </td>",
"      <td>",
"       Anemia",
"      </td>",
"      <td>",
"       25 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       RA with ring sideroblasts (RARS)",
"      </td>",
"      <td>",
"       &lt;5 percent",
"      </td>",
"      <td>",
"       Anemia, &ge;15 percent ringed sideroblasts in erythroid precursors",
"      </td>",
"      <td>",
"       5 to 20 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       MDS with isolated del(5q) (5q- syndrome)",
"      </td>",
"      <td>",
"       &lt;5 percent",
"      </td>",
"      <td>",
"       Anemia, normal platelets",
"      </td>",
"      <td>",
"       100 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Refractory cytopenia with multilineage dysplasia (RCMD)",
"      </td>",
"      <td>",
"       &lt;5 percent",
"      </td>",
"      <td>",
"       <p>",
"        Bicytopenia or pancytopenia",
"       </p>",
"       <p>",
"        &plusmn;15 ring sideroblasts",
"       </p>",
"      </td>",
"      <td>",
"       50 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Refractory anemia with excess blasts-1",
"      </td>",
"      <td>",
"       5 to 9 percent",
"      </td>",
"      <td>",
"       Cytopenias +/- peripheral blood blasts (&lt;5 percent)",
"      </td>",
"      <td>",
"       30 to 50 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Refractory anemia with excess blasts-2",
"      </td>",
"      <td>",
"       10 to 19 percent",
"      </td>",
"      <td>",
"       Cytopenias, peripheral blood blasts present",
"      </td>",
"      <td>",
"       50 to 70 percent",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Myelodysplastic syndrome, unclassified",
"      </td>",
"      <td>",
"       &lt;5 percent",
"      </td>",
"      <td>",
"       Neutropenia or thrombocytopenia",
"      </td>",
"      <td>",
"       50 percent",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MDS: myelodysplastic syndrome.",
"    </div>",
"    <div class=\"reference\">",
"     Source: Vardiman, JW, Brunning, RD, Arber, DA, et al. Introduction and overview of the classification of myeloid neoplasms. In: WHO classification of tumors of hematopoietic and lymphoid tissues, Swerdlow, SH, Campo, E, Harris, NL, et al. (Eds), WHO Press, 2008. p.18.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
