var title_f9_52_10059="PHACE syndrome";
var content_f9_52_10059=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F80245&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F80245&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of PHACE syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        P",
"       </td>",
"       <td>",
"        Posterior fossa brain malformations, most commonly the Dandy-Walker variant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        H",
"       </td>",
"       <td>",
"        Hemangiomas, particularly large, segmental facial lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        A",
"       </td>",
"       <td>",
"        Arterial anomalies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        C",
"       </td>",
"       <td>",
"        Cardiac anomalies and coarctation of the aorta",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        E",
"       </td>",
"       <td>",
"        Eye abnormalities and endocrine abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        S*",
"       </td>",
"       <td>",
"        Sternal cleft, supraumbilical raphe, or both",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * The term \"PHACE(S)\" is sometimes used in the presence of ventral developmental defects.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
