var title_f9_52_10060="Classification of teratomas";
var content_f9_52_10060=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F59492&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F59492&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of extragonadal teratomas",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        I. Benign teratomas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Mature",
"teratoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Grade 0 - All component tissues appear well",
"differentiated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Grade 1 - Occasional microscopic foci contain",
"incompletely differentiated tissues, not exceeding 10 percent of the",
"sampled surface",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Immature",
"teratoma, benign",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Grade 2 - Immature tissues make up between 10 and 50",
"percent of the sampled tumor surface",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Grade 3 - Over half of the surface examined is composed",
"of undifferentiated tissues of uncertain metastatic potential; a benign",
"course is still possible",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        II. Malignant",
"teratomas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        With",
"areas of germ cell tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Germinoma (seminoma, dysgerminoma)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Embryonal carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Choriocarcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Yolk sac tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mixed (any combination of the above)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        With",
"nongerminal malignant tumor pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Carcinoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sarcoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Malignant embryonal tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mixed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Immature",
"teratoma, malignant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A teratoma that would otherwise be classified as benign",
"immature teratoma, but which subsequently became metastatic",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Gonzalez-Crussi, F. Atlas of Tumor Pathology: Extragonadal Teratomas, 2nd ed, Armed Forces Institute of Pathology, Washington, DC, 1982.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
