var title_f9_55_10107="Adjustments for missed appointments";
var content_f9_55_10107=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F59180&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F59180&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Adjustments in subcutaneous immunotherapy dosing for gaps in treatment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Build-up phase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Up to 7 days late",
"       </td>",
"       <td>",
"        Continue build-up as scheduled",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        8 to 13 days late",
"       </td>",
"       <td>",
"        Repeat previous dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        14 to 21 days late",
"       </td>",
"       <td>",
"        Reduce dose 25 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        21 to 28 days late",
"       </td>",
"       <td>",
"        Reduce dose 50 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Maintenance phase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2 to 4 weeks late",
"       </td>",
"       <td>",
"        Reduce dose 75 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &gt;4 weeks late",
"       </td>",
"       <td>",
"        Reduce by&nbsp;one or more dilutions depending on length of time and patient sensitivity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
