var title_f9_57_10140="Oral antibiotics for infections due to MRSA";
var content_f9_57_10140=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F64178&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F64178&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Options for oral treatment of methicillin-resistant Staphylococcus aureus (MRSA)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Treatment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adult dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pediatric dose (children &gt;28 days)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin",
"       </td>",
"       <td>",
"        300 to 450 mg orally three times daily",
"       </td>",
"       <td>",
"        40 mg/kg per day orally divided in three or four doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Trimethoprim-sulfamethoxazole",
"       </td>",
"       <td>",
"        1 DS tab orally twice daily",
"       </td>",
"       <td>",
"        8 to 12 mg trimethoprim component/kg per day orally divided in two doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Doxycycline",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        100 mg orally twice daily",
"       </td>",
"       <td>",
"        <p>",
"         &le;45 kg: 4 mg/kg per day orally divided in two doses",
"        </p>",
"        <p>",
"         &gt;45 kg: 100 mg orally twice daily",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minocycline",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        200 mg orally once, then 100 mg orally twice daily",
"       </td>",
"       <td>",
"        4 mg/kg orally once, then 4 mg/kg per day divided in two doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Linezolid",
"       </td>",
"       <td>",
"        600 mg orally twice daily",
"       </td>",
"       <td>",
"        <p>",
"         &lt;12 years: 30 mg/kg per day orally divided in three doses",
"        </p>",
"        <p>",
"         &ge;12 years: 600 mg orally twice daily",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;Tedizolid",
"       </td>",
"       <td>",
"        &nbsp;200 mg orrally once daily",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     DS: double strength.",
"     <br/>",
"     * Dosing for neonates is provided separately. (See table \"Treatment of cellulitis in neonates.\")",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Not recommended for children &lt;8 years of age.",
"    </div>",
"    <div class=\"reference\">",
"     Data adapted from: Liu C, Bayer A, Cosgrove SE, et al. Clinical Practice Guidelines by the Infectious Diseases Society of America for the Treatment of Methicillin-Resistant Staphylococcus Aureus Infections in Adults and Children. Clin Infect Dis 2011; 52:e18.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
