var title_f9_59_10173="Sarcoid monitoring";
var content_f9_59_10173=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F67386&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F67386&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Monitoring disease activity in sarcoidosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients with active disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Every 3 to 4 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ROS including fever, fatigue, weight loss, visual disturbance, dyspnea, cough, palpitations, abdominal pain, numbness, tingling, lightheadedness, syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PE including skin exam, palpation of lymph nodes, lung exam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lab tests based on sites of disease activity and medications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Spirometry, diffusing capacity, ambulatory oximetry (or 6 minute walk)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Every 6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ophthalmologic exam, if on hydroxychloroquine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Every 12 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CBC and differential",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Creatinine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Calcium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        AST, ALT, Alkaline phosphatase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        25 hydroxy vitamin D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1,25 dihydroxy vitamin D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ACE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        EKG, sooner if palpitations, lightheadedness, syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ophthalmologic exam (slit lamp, fundoscopic, tonometric), sooner if visual disturbance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest x-ray",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        As indicated by symptoms or other tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HRCT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiogram",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Holter monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinalysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ACE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TSH (association of thyroid autoimmunity and sarcoidosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone density when glucocorticoid therapy initiated and then every 3 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MRI with gadolinium if neurologic symptoms (unless diabetic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patients with inactive disease for 2 or more (?) years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Every 12 to 18 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ROS including fever, fatigue, weight loss, visual disturbance, dyspnea, cough, palpitations, abdominal pain, numbness, tingling, lightheadedness, syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PE including skin exam, palpation of lymph nodes, lung exam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Spirometry, diffusing capacity, ambulatory oximetry (or 6 minute walk)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CBC and differential",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Creatinine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Calcium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Liver enzymes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1,25 dihydroxy vitamin D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        EKG, sooner if palpitations, lightheadedness, syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ophthalmologic exam, sooner if visual disturbance",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ROS: review of systems; PE: physical exam; CBC: complete blood count; AST: aspartate aminotransferase; ALT: alanine aminotransferase; ACE: angiotensin converting enzyme; EKG: electrocardiogram; HRCT: high resolution computed tomography; TSH: thyroid stimulating hormone; MRI: magnetic resonance imaging.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
