" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pelvic organ prolapse in women: Choosing a primary surgical procedure",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/7/9337/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/7/9337/contributors\">",
"     J Eric Jelovsek, MD, MMEd, FACOG, FACS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/7/9337/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/7/9337/contributors\">",
"     Linda Brubaker, MD, FACS, FACOG",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?9/7/9337/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?9/7/9337/contributors\">",
"     Sandy J Falk, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
