var title_f9_7_9339="DSM-IV-TR diagnostic criteria for anorexia nervosa";
var content_f9_7_9339=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F75110&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F75110&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV-TR diagnostic criteria for anorexia nervosa",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Refusal to maintain body weight at or above a minimally normal weight for age and height (eg, weight loss or failure to gain weight that leads to a body weight less than 85 percent of that expected for age and height).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intense fear of gaining weight or becoming fat, even though underweight.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Disturbed perception of one's body weight or shape, undue influence of weight or shape on self-evaluation, or denial of the seriousness of the current low body weight.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In postmenarcheal females, amenorrhea, ie, absence of at least three consecutive menstrual cycles. Menstruation that occurs only after hormonal treatment, eg, estrogen, is considered amenorrhea.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th Ed, Text Revision, Washington, DC 2000. p.589.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
